require 'test_helper'

class ResponseParserTest < ActiveSupport::TestCase
  def setup
    json = JSON.parse(fda_response.to_json, symbolize_names:true)
    @response = json[:results].first
  end

  test "parses FDA response correctly" do
    rp = ResponseParser.new(@response)

    assert rp.nursing_mothers
    assert_includes rp.description, "DESCRIPTION"
    assert_equal "SERTRALINE HYDROCHLORIDE", rp.substance_name
    assert_equal "Aurobindo Pharma Limited", rp.manufacturer_name
    assert_includes rp.dosage, "20 mg/mL of sertraline"
  end

  test "response if data is nil" do
    rp = ResponseParser.new(@response)

    assert_equal "No data to display", rp.usage
  end

